class Ciudad extends React.Component {

    constructor (props) {
        super (props);
        this.state = {
            ciudad: {}
        }
    }

    componentDidMount () {
        const apiKey = '4ae2636d8dfbdc3044bede63951a019b';
        const id = this.props.match.params.ciudadId;
        fetch(`http://api.openweathermap.org/data/2.5/weather?q=${id}&appid=${apiKey}&units=metric`)
        .then (r => r.json ())
        .then ((recurso) => {
            this.setState ({
                ciudad: recurso
            })
        })
    }

    render () {
        const city = this.state.ciudad;
        if (!city.main) return <img src={`https://airgways.files.wordpress.com/2019/08/62d987_25414a5d0d0d43a3bc6fc33f9b7e2e4b_mv2.gif?w=254`}></img>
        
    }
}
