import React from "react";
import './Ciudad.css'
/* export default function Ciudad({ city }) {
    
    if (city.length === 1) {
        return (
            <div className="ciudad">
                <div className="container">
                    <h2 id="cityName">{city [0].name}</h2>
                    <div className="info">
                        <div className="info1"><span className="infoTitle">Temperature üå°Ô∏è</span> {city [0].temp} ¬∫C</div>
                        <div className="info1"><span className="infoTitle">Weather ‚òÄÔ∏è</span> {city [0].weather}</div>
                        <div className="info1"><span className="infoTitle">Wind üí®</span> {city [0].wind} km/h</div>
                        <div className="info1"><span className="infoTitle">Cloud quantity ‚òÅÔ∏è</span> {city [0].clouds}</div>
                        <div className="info1"><span className="infoTitle">Latitude üåê</span> {city [0].latitud}¬∫</div>
                        <div className="info1"><span className="infoTitle">Longitude üß≠</span> {city [0].longitud}¬∫</div> 
                    </div>
                </div>
            </div>
        )
    } else {
        return (
            <span id="errorMessage">Oops! Nothing to show here!</span>
        )
    }
} */

class Ciudad extends React.Component {
    constructor(props) {
        this.state = {
            city: {}
        }
    }

    componentDidMount() {
        const apiKey = '4ae2636d8dfbdc3044bede63951a019b';
        var id = this.props.match.id;
        fetch(`http://api.openweathermap.org/data/2.5/weather?q=${id}&appid=${apiKey}&units=metric`)
            .then(r => r.json())
            .then((recurso) => {
                this.setState = {
                    city: recurso
                }
            })
    }

    render() {
        const city = this.state.city;
        if (!this.state.city.main) {
            return (
                <img src={`https://airgways.files.wordpress.com/2019/08/62d987_25414a5d0d0d43a3bc6fc33f9b7e2e4b_mv2.gif?w=254`}></img>
            )
        } else {
            return (
                <div className="ciudad">
                    <div className="container">
                        <h2 id="cityName">{city[0].name}</h2>
                        <div className="info">
                            <div className="info1"><span className="infoTitle">Weather ‚òÄÔ∏è</span> {city[0].weather}</div>
                            <div className="info1"><span className="infoTitle">Wind üí®</span> {city[0].wind} km/h</div>
                            <div className="info1"><span className="infoTitle">Cloud quantity ‚òÅÔ∏è</span> {city[0].clouds}</div>
                            <div className="info1"><span className="infoTitle">Latitude üåê</span> {city[0].latitud}¬∫</div>
                            <div className="info1"><span className="infoTitle">Longitude üß≠</span> {city[0].longitud}¬∫</div>
                        </div>
                    </div>
                </div>
            )
        }
    }
}

export default Ciudad
